import streamlit as st
import requests
import json
import pyautogui

st.set_page_config(page_icon="ðŸ’°", page_title="Crypto Price Report")
st.title("ðŸ’° Crypto Price Report")

moneda = st.selectbox("Elige una Cripto",["bitcoin","dai","ethereum","cardano"])
tipo = st.selectbox("Elige una moneda",["MXN","USD","EUR"])

api_url = "https://api.coingecko.com/api/v3/simple/price?ids="+moneda+"&vs_currencies="+tipo
response = requests.get(api_url)

respuesta = response.json()
json_data = json.dumps(respuesta)

if moneda == 'bitcoin':
    st.image("data:image/png;base64,",width=90)
elif moneda == 'ethereum':
    st.image("data:image/jpeg;base64,",width=50)
elif moneda == 'dai':
    st.image("data:image/jpeg;base64,",width=50)
elif moneda == 'cardano':
    st.image("data:image/png;base64,",width=100)

if moneda == "bitcoin":
    if  float(json_data.split(":")[2].replace("}","")) > 20000:
        st.success("BITCOINC PRICE: $" + json_data.split(":")[2].replace("}",""))
    elif float(json_data.split(":")[2].replace("}","")) < 16000:
        st.error("BITCOINC PRICE: $" + json_data.split(":")[2].replace("}",""))
    else:
     st.warning("BITCOINC PRICE: $" + json_data.split(":")[2].replace("}",""))
else:
    st.success(moneda + " PRICE: $" + json_data.split(":")[2].replace("}",""))

if st.button("Refresh Price"):
    pyautogui.hotkey("ctrl","F5")

st.json(response.json())